import { combineReducers } from 'redux';

// Initial States
const initialUserState = {
  loading: false,
  isAuthenticated: false,
  user: null,
  error: null,
  message: null
};

const initialProfileState = {
  loading: false,
  error: null,
  message: null
};

const initialSubscriptionState = {
  loading: false,
  subscriptionId: null,
  error: null,
  message: null
};

// User Reducer
function userReducer(state = initialUserState, action) {
  switch (action.type) {
    case 'LOGIN_REQUEST':
    case 'REGISTER_REQUEST':
    case 'LOGOUT_REQUEST':
    case 'LOAD_USER_REQUEST':
      return {
        ...state,
        loading: true
      };
    case 'LOGIN_SUCCESS':
    case 'REGISTER_SUCCESS':
      return {
        ...state,
        loading: false,
        isAuthenticated: true,
        user: action.payload.user,
        message: action.payload.message
      };
    case 'LOGIN_FAIL':
    case 'REGISTER_FAIL':
    case 'LOAD_USER_FAIL':
      return {
        ...state,
        loading: false,
        isAuthenticated: false,
        error: action.payload
      };
    case 'LOGOUT_SUCCESS':
      return {
        ...state,
        loading: false,
        isAuthenticated: false,
        user: null,
        message: action.payload
      };
    case 'LOGOUT_FAIL':
      return {
        ...state,
        loading: false,
        isAuthenticated: true,
        error: action.payload
      };
    case 'LOAD_USER_SUCCESS':
      return {
        ...state,
        loading: false,
        isAuthenticated: true,
        user: action.payload
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null
      };
    case 'CLEAR_MESSAGE':
      return {
        ...state,
        message: null
      };
    default:
      return state;
  }
}

// Profile Reducer
function profileReducer(state = initialProfileState, action) {
  switch (action.type) {
    case 'UPDATE_PROFILE_REQUEST':
    case 'UPDATE_PROFILE_PICTURE_REQUEST':
    case 'CHANGE_PASSWORD_REQUEST':
    case 'FORGET_PASSWORD_REQUEST':
    case 'RESET_PASSWORD_REQUEST':
    case 'REMOVE_FROM_PLAYLIST_REQUEST':
      return {
        ...state,
        loading: true
      };
    case 'UPDATE_PROFILE_SUCCESS':
    case 'UPDATE_PROFILE_PICTURE_SUCCESS':
    case 'CHANGE_PASSWORD_SUCCESS':
    case 'FORGET_PASSWORD_SUCCESS':
    case 'RESET_PASSWORD_SUCCESS':
    case 'REMOVE_FROM_PLAYLIST_SUCCESS':
      return {
        ...state,
        loading: false,
        message: action.payload
      };
    case 'UPDATE_PROFILE_FAIL':
    case 'UPDATE_PROFILE_PICTURE_FAIL':
    case 'CHANGE_PASSWORD_FAIL':
    case 'FORGET_PASSWORD_FAIL':
    case 'RESET_PASSWORD_FAIL':
    case 'REMOVE_FROM_PLAYLIST_FAIL':
      return {
        ...state,
        loading: false,
        error: action.payload
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null
      };
    case 'CLEAR_MESSAGE':
      return {
        ...state,
        message: null
      };
    default:
      return state;
  }
}

// Subscription Reducer
function subscriptionReducer(state = initialSubscriptionState, action) {
  switch (action.type) {
    case 'BUY_SUBSCRIPTION_REQUEST':
    case 'CANCEL_SUBSCRIPTION_REQUEST':
      return {
        ...state,
        loading: true
      };
    case 'BUY_SUBSCRIPTION_SUCCESS':
      return {
        ...state,
        loading: false,
        subscriptionId: action.payload
      };
    case 'CANCEL_SUBSCRIPTION_SUCCESS':
      return {
        ...state,
        loading: false,
        message: action.payload
      };
    case 'BUY_SUBSCRIPTION_FAIL':
    case 'CANCEL_SUBSCRIPTION_FAIL':
      return {
        ...state,
        loading: false,
        error: action.payload
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null
      };
    case 'CLEAR_MESSAGE':
      return {
        ...state,
        message: null
      };
    default:
      return state;
  }
}

// Combine Reducers
const rootReducer = combineReducers({
  user: userReducer,
  profile: profileReducer,
  subscription: subscriptionReducer
});

export default rootReducer;
