import { Box, Heading, HStack, Stack, VStack ,Link} from '@chakra-ui/react';
import React from 'react';
import {
  TiSocialYoutubeCircular,
  TiSocialInstagramCircular,
} from 'react-icons/ti';
import { DiGithub } from 'react-icons/di';
const Footer = () => {
  return (
    <Box padding={'4'} bg="blackAlpha.900" minH={'10vh'}>
      <Stack direction={['column', 'row']}>
        <VStack alignItems={['center', 'flex-start']} width="full">
          <Heading children="All Rights Reserved" color={'white'} />
          <Heading
            fontFamily={'body'}
            size="sm"
            children="@rahulyadav"
            color={'yellow.400'}
          />
          <Heading
    fontFamily="body"
    size="sm"
    
  >

 
    <Link href=" /about" color="yellow.300" textDecoration="none">Privacy Policy</Link>
    {' / '}
    <Link href=" /about " color="yellow.300" textDecoration="none">Terms and Conditions</Link>
    {' / '}
    <Link href=" /about" color="yellow.300" textDecoration="none">Refund & Cancellation Policy</Link>
  </Heading>

          
    
        </VStack>

      

        <HStack
          spacing={['2', '10']}
          justifyContent="center"
          color={'white'}
          fontSize="50"
        >
          <a href="" target={'blank'}>
            <TiSocialYoutubeCircular />
          </a>
          <a href="" target={'blank'}>
         
            <TiSocialInstagramCircular />
          </a>
          <a href="https://github.com/C0DER-RAHUL" target={'blank'}>
            <DiGithub />
          </a>
        </HStack>
      </Stack>
    </Box>
  );
};

export default Footer;
